func on_attack(target):
	var card = Main.cards[self.enemy][target]
	card.structure -= max(card.shield-self.damage,0)

func on_attack(target):
	var card = Main.cards[self.enemy][target]
	card.structure -= max(card.shield-self.damage,0)

func on_use(target):
	var card = Main.cards[self.owner][target]
	var max_structure = Data.calc_value(card.type,'structure')
	card.structure = min(card.structure+5,max_structure)
	card.update()
	card.node.add_child(load('res://scenes/effects/repair04.tscn').instance())

func on_next_turn(player):
	if (player!=self.owner):
		return
	var pos = -1
	for y in range(POSITIONS):
		if (cards[self.owner][self.position*POSITIONS+y]==null):
			pos = self.position*POSITIONS+y
			break
	if (pos>=0):
		Main.add_unit(pos,'light_fighter',owner)
